sys_script_include.script:
var TMFProductOrderPayloadGenerationService = Class.create();
TMFProductOrderPayloadGenerationService.prototype = {
    initialize: function() {
        this.utilsSI = new x_648117_tmf_mock.Utils();
        this.tmfPayloadUtilsSI = new x_648117_tmf_mock.TMFPayloadUtils();
    },

	/**
     * Determines helper function to run based on the TMF622 method selected in the payload generator
     * 
     * @param payloadGeneratorGr {GlideRecord object} - The payload generator record
     * @return {null}
     **/
    initializePayloadGenerator(payloadGeneratorGr) {
        if (typeof(payloadGeneratorGr) !== "object") {
            return null;
        }

        var api = payloadGeneratorGr.getValue('method_and_endpoint');
        switch (api) {
            case "post_sn_ind_tmt_orm_order_productOrder":
                return this._generatePayload(payloadGeneratorGr.getUniqueValue());
			default:
				gs.error(gs.getMessage("Switch statement in 'TMFProductOrderPayloadGenerationService' Script Include found no match"));
				return null;
        }
    },

    /**
     * Generates the TMF622 compliant payload
     * 
     * @param payloadGeneratorSysId {string} - The payload generator record to use in generating the payload
     * @return {string} - The TMF622 compliant payload
     **/
    _generatePayload(payloadGeneratorSysId) {
        var payload = {};
        var rootElements = ['orderCurrency', 'externalId', 'requestedCompletionDate', 'requestedStartDate'];
        var noteArray = [];
        var channel = '';
        var account = '';
        var contact = '';

        var coreOrderDataGr = this.utilsSI.getRecords('x_648117_tmf_mock_core_order_data', 'payload_generator=' + payloadGeneratorSysId);
        if (coreOrderDataGr) {
            while (coreOrderDataGr.next()) {
                var attribute = coreOrderDataGr.getValue('attribute');
                var attributeValue = coreOrderDataGr.getValue('value');
                var recordValue = coreOrderDataGr.getValue('record');

                if (rootElements.indexOf(attribute) > -1) {
                    if (attributeValue) {
                        payload[attribute] = attributeValue;
                    }
                }

                if (attribute === 'channel') {
                    channel = recordValue;
                }
                if (attribute === 'note' && attributeValue !== null) {
                    noteArray.push({
                        "text": attributeValue
                    });
                }
                if (attribute === 'account') {
                    account = recordValue;
                }
                if (attribute === 'contact') {
                    contact = recordValue;
                }
            }
        }
		if (noteArray.length > 0){
			payload.note = noteArray;
		}
		if (this._generateProductOrderItemArray(payloadGeneratorSysId).length > 0){
			payload.productOrderItem = this._generateProductOrderItemArray(payloadGeneratorSysId);
		}
		if (Array.isArray(this.tmfPayloadUtilsSI.generateRelatedPartyArray(contact, account))){
			payload.relatedParty = this.tmfPayloadUtilsSI.generateRelatedPartyArray(contact, account);	
		}
		if (Array.isArray(this.tmfPayloadUtilsSI.generateChannelArray(channel))){
			payload.channel = this.tmfPayloadUtilsSI.generateChannelArray(channel);
		}
        payload['@type'] = 'ProductOrder';
        return JSON.stringify(payload);
    },

    /**
     * Generates the product order item array for TMF622
     * 
     * @param payloadGeneratorSysId {string} - The payload generator record to use in generating the array
     * @return {array} - The product order item array
     **/
    _generateProductOrderItemArray(payloadGeneratorSysId) {
        var array = [];
        var orderableItemsGr = this.utilsSI.getRecords('x_648117_tmf_mock_items', 'payload_generator=' + payloadGeneratorSysId);
        if (orderableItemsGr) {
            while (orderableItemsGr.next()) {
				// Boolean is stored as string of 0 or 1
                if (orderableItemsGr.getValue('unique_line_item') === "1") {
                    for (i = 1; i < parseInt(orderableItemsGr.getValue('quantity')) + 1; i++) {
                        array.push(this._generateProductOrderItemObj(orderableItemsGr, i));
                    }
                } else {
                    array.push(this._generateProductOrderItemObj(orderableItemsGr));
                }
            }
        }
        return array;
    },

    /**
     * Generates the product order item object for an item
     * @param orderableItemsGr {GlideRecord object} - The item used in generating the object
     * @param i {integer} - Optionally, an iterator to ensure duplicate items have unique ids
     * @return {object} - The product order item object
     **/
    _generateProductOrderItemObj(orderableItemsGr, i) {
        var obj = {};
        obj.action = orderableItemsGr.getValue('action');
		// Boolean is stored as string of 0 or 1
        if (orderableItemsGr.getValue('unique_line_item') === "1") {
            if ((i != "") && (i != null)) {
                obj.id = orderableItemsGr.getUniqueValue() + '_' + i;
            } else {
                obj.id = orderableItemsGr.getUniqueValue();
            }
            obj.quantity = 1;
        } else {
            obj.id = orderableItemsGr.getUniqueValue();
            obj.quantity = orderableItemsGr.getValue('quantity');
        }

        if (this.tmfPayloadUtilsSI.checkForPrice(orderableItemsGr) === true) {
            var itemPriceArray = [];
            if (orderableItemsGr.getValue('mrc') !== "0") {
                itemPriceArray.push(this.tmfPayloadUtilsSI.generateItemPriceObj(orderableItemsGr, 'mrc'));
            }
            if (orderableItemsGr.getValue('nrc') !== "0") {
                itemPriceArray.push(this.tmfPayloadUtilsSI.generateItemPriceObj(orderableItemsGr, 'nrc'));
            }
            obj.itemPrice = itemPriceArray;
        }
        obj.product = this._generateProductObj(orderableItemsGr);
        obj.productOffering = this.tmfPayloadUtilsSI.generateProductOfferingObj(orderableItemsGr.getValue('product_offering'));
        if (this.tmfPayloadUtilsSI.checkForProductOrderItemRelationship(orderableItemsGr) === true) {
            obj.productOrderItemRelationship = this.tmfPayloadUtilsSI.generateProductOrderItemRelationshipArray(orderableItemsGr);
        }
        obj["@type"] = "ProductOrderItem";
        return obj;
    },

    /**
     * Generates the product object for an item
     * @param orderableItemsGr {GlideRecord object} - The item used in generating the object
     * @return {object} - The product object
     **/
    _generateProductObj(orderableItemsGr) {
        var obj = {};
        if (orderableItemsGr.getValue('action') !== "add") {
            obj.id = this.tmfPayloadUtilsSI.getRecordIdentifier('sn_prd_invt_product_inventory', orderableItemsGr.getValue('product_inventory'));
        }
        obj["@type"] = "Product";
        if (this.tmfPayloadUtilsSI.checkForProductCharacteristics(orderableItemsGr) === true) {
            obj.productCharacteristic = this.tmfPayloadUtilsSI.generateProductCharacteristicArray(orderableItemsGr);
        }
        obj.productSpecification = this.tmfPayloadUtilsSI.generateProductSpecificationObj(orderableItemsGr.getValue('specification'));
        if (!orderableItemsGr.location.nil()) {
            obj.place = this.tmfPayloadUtilsSI.generatePlaceObj(orderableItemsGr.getValue('location'));
        }
        if (this.tmfPayloadUtilsSI.checkForProductRelationship(orderableItemsGr) === true) {
            obj.productRelationship = this.tmfPayloadUtilsSI.productRelationshipArray(orderableItemsGr);
        }
        if (this.tmfPayloadUtilsSI.checkForProductRelatedParty(orderableItemsGr) === true) {
            obj.relatedParty = this.tmfPayloadUtilsSI.generateProductRelatedPartyArray(orderableItemsGr);
        }
        return obj;
    },
    type: 'TMFProductOrderPayloadGenerationService'
};
