sys_script_include.script:
var CoreOrderDataUtils = Class.create();
CoreOrderDataUtils.prototype = {
    initialize: function() {
        this.utilsSI = new x_648117_tmf_mock.Utils();
    },

	/**
     * Checks if an attribute utilizes a reference to a record in the system vs. a free form string
     * 
     * @param attribute {string} - The attribute as defined by its choice value
     * @return {boolean} - True or False based on the check. True denotes the attributes uses a reference
     **/
    checkAttributeForReference(attribute){
		if (attribute === undefined){
			return false;
		}

		var check = false;
		var map = Constants.CORE_DATA_ATTRIBUTE_MAP;
		var attributeObj = map.find(obj => obj.field == attribute);
		if(attributeObj.hasOwnProperty('reference')){
			if(attributeObj.reference == true){
				check = true;
			}
		}
		return check;
	},

    type: 'CoreOrderDataUtils'
};
