var InitializationUtils = Class.create();
InitializationUtils.prototype = {
    initialize: function() {
        this.utilsSI = new x_648117_tmf_mock.Utils();
    },

	/**
     * Get the default value from an object
     * 
     * @param obj {object} - An object with a 'default' property
     * @return {string} - The default value
     **/
	getParamDefaultValue(obj){
		if (!obj.hasOwnProperty('default') || obj.default === ""){
			return null;
		}

		return obj.default.toString();
	},
    type: 'InitializationUtils'
};
