sys_script_include.script:
var TMFPayloadUtils = Class.create();
TMFPayloadUtils.prototype = {
    initialize: function() {
        this.utilsSI = new x_648117_tmf_mock.Utils();
    },

    /**
     * Generates the TMF compliant channel array
     * 
     * @param channelSysId {string} - The channel record to use in generating the array as a sys_id
     * @return {array} - The TMF compliant channel array
     **/
    generateChannelArray(channelSysId) {
        var recordGr = this.utilsSI.getRecordFromSysId('sn_prd_pm_distribution_channel', channelSysId);
        if (!recordGr) {
            return null;
        }

        return [{
            "id": this.getRecordIdentifier('sn_prd_pm_distribution_channel', channelSysId),
            "name": recordGr.getValue('name')
        }];
    },

    /**
     * Generates the TMF compliant related party array
     * 
     * @param contactSysId {string} - The contact record to use in generating the array as a sys_id
     * @param accountSysId {string} - The account record to use in generating the array as a sys_id
     * @return {array} - The TMF compliant related party array
     **/
    generateRelatedPartyArray(contactSysId, accountSysId) {
        var array = [];
        if (this._generateCustomerContactObj(contactSysId) != null) {
            array.push(this._generateCustomerContactObj(contactSysId));
        }
        if (this._generateCustomerObj(accountSysId) != null) {
            array.push(this._generateCustomerObj(accountSysId));
        }

        if (array.length == 0) {
            return null;
        } else {
            return array;
        }
    },

    /**
     * Generates the TMF compliant customer contact object
     * 
     * @param contactSysId {string} - The contact record to use in generating the object as a sys_id
     * @return {object} - The TMF compliant customer contact object
     **/
    _generateCustomerContactObj(contactSysId) {
        var recordGr = this.utilsSI.getRecordFromSysId('customer_contact', contactSysId);
        if (!recordGr) {
            return null;
        }

        var obj = {};
        obj.id = this.getRecordIdentifier('customer_contact', contactSysId);
        obj.name = recordGr.getValue('name');
        obj['@type'] = 'RelatedParty';
        obj['@referredType'] = 'CustomerContact';
        return obj;
    },

    /**
     * Generates the TMF compliant customer object
     * 
     * @param accountSysId {string} - The account record to use in generating the object as a sys_id
     * @return {object} - The TMF compliant customer object
     **/
    _generateCustomerObj(accountSysId) {
        var recordGr = this.utilsSI.getRecordFromSysId('customer_account', accountSysId);
        if (!recordGr) {
            return null;
        }

        var obj = {};
        obj.id = this.getRecordIdentifier('customer_account', accountSysId);
        obj.name = recordGr.getValue('name');
        obj['@type'] = 'RelatedParty';
        obj['@referredType'] = 'Customer';
        return obj;
    },

    /**
     * Determines if an item has characteristics
     * 
     * @param orderableItemGr {GlideRecord object} - The item to check
     * @return {boolean} - True/False denoting if the item has characteristics
     **/
    checkForProductCharacteristics(orderableItemGr) {
        var productCharacteristicsGr = this.utilsSI.getRecords('x_648117_tmf_mock_item_characteristic', 'item=' + orderableItemGr.getUniqueValue());
        if (productCharacteristicsGr) {
            return true;
        }
        return false;
    },

    /**
     * Generates the TMF compliant product characteristic array
     * 
     * @param orderableItemGr {GlideRecord object} - The item to use in generating the array
     * @return {array} - The TMF compliant product characteristic array
     **/
    generateProductCharacteristicArray(orderableItemGr) {
        var array = [];
        var productCharacteristicsGr = this.utilsSI.getRecords('x_648117_tmf_mock_item_characteristic', 'item=' + orderableItemGr.getUniqueValue());
        if (productCharacteristicsGr) {
            while (productCharacteristicsGr.next()) {
                var characteristicDefinitionGr = this.utilsSI.getRecordFromSysId('sn_prd_pm_characteristic', productCharacteristicsGr.getValue('characteristic'));
                array.push({
                    "name": characteristicDefinitionGr.getValue('name'),
                    "value": productCharacteristicsGr.getValue('characteristic_value')
                });
            }
        }
        return array;
    },

    /**
     * Generates the TMF compliant product specification object
     * 
     * @param productSpecificationSysId {string} - The specification to use in generating the object as sys_id
     * @return {object} - The TMF compliant product specification object
     **/
    generateProductSpecificationObj(productSpecificationSysId) {
        var recordGr = this.utilsSI.getRecordFromSysId('sn_prd_pm_product_specification', productSpecificationSysId);
        if (!recordGr) {
            return null;
        }

        var obj = {};

        if (recordGr.getValue('external_id') != null) {
            obj.id = recordGr.getValue('external_id');
        } else {
            obj.id = recordGr.getValue('initial_version');
        }
        obj.internalVersion = recordGr.getValue('version');
        obj.name = recordGr.getValue('name');
        if (recordGr.getValue('external_version') != null) {
            obj.version = recordGr.getValue('external_version');
        }
        obj['@type'] = 'ProductSpecificationRef';
        return obj;
    },

    /**
     * Generates the TMF compliant location object
     * 
     * @param locationSysId {string} - The location to use in generating the object as sys_id
     * @return {object} - The TMF compliant location object
     **/
    generatePlaceObj(locationSysId) {
        var obj = {};
        obj.id = this.getRecordIdentifier('cmn_location', locationSysId);
        obj['@type'] = 'Place';
        return obj;
    },

    /**
     * Determines if an item has product item relationships
     * 
     * @param orderableItemGr {GlideRecord object} - The item to check
     * @return {boolean} - True/False denoting if the item has product item relationships
     **/
    checkForProductOrderItemRelationship(orderableItemGr) {
        var productOrderItemRelationshipsGr = this.utilsSI.getRecords('x_648117_tmf_mock_item_relationship', 'source_item=' + orderableItemGr.getUniqueValue() + '^relationship_class=productOrderItemRelationship');
        if (productOrderItemRelationshipsGr) {
            return true;
        }
        return false;
    },

    /**
     * Generates the TMF compliant product item relationship array
     * 
     * @param orderableItemGr {GlideRecord object} - The item to use in generating the array
     * @return {array} - The TMF compliant product item relationship array
     **/
    generateProductOrderItemRelationshipArray(orderableItemGr) {
        var array = [];
        var productOrderItemRelationshipsGr = this.utilsSI.getRecords('x_648117_tmf_mock_item_relationship', 'source_item=' + orderableItemGr.getUniqueValue() + '^relationship_class=productOrderItemRelationship');
        if (productOrderItemRelationshipsGr) {
            while (productOrderItemRelationshipsGr.next()) {
                array.push({
                    "id": productOrderItemRelationshipsGr.getValue('target_item'),
                    "relationshipType": productOrderItemRelationshipsGr.getValue('relationship_type')
                });
            }
        }
        return array;
    },

    /**
     * Determines if an item has product relationships
     * 
     * @param orderableItemGr {GlideRecord object} - The item to check
     * @return {boolean} - True/False denoting if the item has product relationships
     **/
    checkForProductRelationship(orderableItemGr) {
        var productRelationshipsGr = this.utilsSI.getRecords('x_648117_tmf_mock_item_relationship', 'source_item=' + orderableItemGr.getUniqueValue() + '^relationship_class=productRelationship');
        if (productRelationshipsGr) {
            return true;
        }
        return false;
    },

    /**
     * Generates the TMF compliant product relationship array
     * 
     * @param orderableItemGr {GlideRecord object} - The item to use in generating the array
     * @return {array} - The TMF compliant product relationship array
     **/
    generateProductRelationshipArray(orderableItemGr) {
        var array = [];
        var productRelationshipsGr = this.utilsSI.getRecords('x_648117_tmf_mock_item_relationship', 'source_item=' + orderableItemGr.getUniqueValue() + '^relationship_class=productRelationship');
        if (productRelationshipsGr) {
            while (productRelationshipsGr.next()) {
                array.push({
                    "id": this.getRecordIdentifier('sn_prd_invt_product_inventory', productRelationshipsGr.getValue('product_inventory')),
                    "relationshipType": productRelationshipsGr.getValue('relationship_type')
                });
            }
        }
        return array;
    },

    /**
     * Determines if an item has related parties
     * 
     * @param orderableItemGr {GlideRecord object} - The item to check
     * @return {boolean} - True/False denoting if the item has related parties
     **/
    checkForProductRelatedParty(orderableItemGr) {
        var relatedPartyGr = this.utilsSI.getRecords('x_648117_tmf_mock_related_party', 'item=' + orderableItemGr.getUniqueValue());
        if (relatedPartyGr) {
            return true;
        }
        return false;
    },

    /**
     * Generates the TMF compliant product related party array
     * 
     * @param orderableItemGr {GlideRecord object} - The item to use in generating the array
     * @return {array} - The TMF compliant product related party array
     **/
    generateProductRelatedPartyArray(orderableItemGr) {
        var array = [];
        var relatedPartyGr = this.utilsSI.getRecords('x_648117_tmf_mock_related_party', 'item=' + orderableItemGr.getUniqueValue());
        if (relatedPartyGr) {
            while (relatedPartyGr.next()) {
                array.push({
                    "email": relatedPartyGr.getValue('email'),
                    "firstName": relatedPartyGr.getValue('first_name'),
                    "lastName": relatedPartyGr.getValue('last_name'),
                    "phone": relatedPartyGr.getValue('phone'),
                    "@referredType": "OrderLineItemContact",
                    "@type": "RelatedParty"
                });
            }
        }
        return array;
    },

    /**
     * Determines if an item has price attributes
     * 
     * @param orderableItemGr {GlideRecord object} - The item to check
     * @return {boolean} - True/False denoting if the item has price attributes
     **/
    checkForPrice(orderableItemGr) {
        var mrc = orderableItemGr.getValue('mrc');
        var nrc = orderableItemGr.getValue('nrc');

        if (mrc == 0 && nrc == 0) {
            return false;
        }
        return true;
    },

    /**
     * Generates the TMF compliant product price object
     * 
     * @param orderableItemGr {GlideRecord object} - The item to use in generating the object
     * @return {object} - The TMF compliant product price object
     **/
    generateItemPriceObj(orderableItemGr, currencyFieldName) {
        if (!orderableItemGr.isValidField(currencyFieldName)) {
            return null;
        }

        var obj = {};
        obj.price = {
            taxIncludedAmount: {
                unit: orderableItemGr[currencyFieldName].getCurrencyCode(),
                value: orderableItemGr.getValue(currencyFieldName)
            }
        };

        if (currencyFieldName == 'mrc') {
            obj.priceType = 'recurring';
            obj.recurringChargePeriod = orderableItemGr.getValue('recurring_charge_period');
        } else if (currencyFieldName == 'nrc') {
            obj.priceType = 'non-recurring';
        }
        return obj;
    },

    /**
     * Generates the TMF compliant product offering object
     * 
     * @param productOfferingSysId {String} - The item to use in generating the object as sys_id
     * @return {object} - The TMF compliant product offering object
     **/
    generateProductOfferingObj(productOfferingSysId) {
        var recordGr = this.utilsSI.getRecordFromSysId('sn_prd_pm_product_offering', productOfferingSysId);
        if (!recordGr) {
            return null;
        }

        var obj = {};
        if (recordGr.getValue('external_id') != null) {
            obj.id = recordGr.getValue('external_id');
        } else {
            obj.id = recordGr.getValue('initial_version');
        }
        obj.internalVersion = recordGr.getValue('version');
        obj.name = recordGr.getValue('name');
        if (recordGr.getValue('external_version') != null) {
            obj.version = recordGr.getValue('external_version');
        }
        return obj;
    },

    /**
     * Gets the record identifier from a record. The identifier is an external id or sys_id
     * 
     * @param tableName {string} - Name of the table to query
     * @param sysId {string} - Record to use in the query as sys_id
     * @return {string} - The record identifier
     **/
    getRecordIdentifier(tableName, sysId) {
		// Array of valid external id field names across all telecom tables
        var externalIdFieldNames = Constants.EXTERNAL_ID_FIELD_NAMES;

        var recordGr = this.utilsSI.getRecordFromSysId(tableName, sysId);
        if (recordGr) {

			// Return external ID if the passed in table has an external ID field
            for (var i = 0; i < externalIdFieldNames.length; i++) {
                if (recordGr.isValidField(externalIdFieldNames[i])) {
                    if (!recordGr[externalIdFieldNames[i]].nil()) {
                        return recordGr.getValue(externalIdFieldNames[i]);
                    }
                }
            }
            return recordGr.getUniqueValue(); // Return unique value if no external ID present
        }
        return null;
    },

    type: 'TMFPayloadUtils'
};
