sys_script_include.script:
var ItemCharUtils = Class.create();
ItemCharUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    validateCharOption: function() {

        var validCharValue = false;
        var validCharOptions = [];

        var item = this.getParameter('sysparm_item');
        var characteristic = this.getParameter('sysparm_char');
        var charValue = this.getParameter('sysparm_char_value');

        var grItem = new GlideRecord('x_648117_tmf_mock_items');
        if (grItem.get(item)) {
            var specification = grItem.getValue('specification');
            var productOffering = grItem.getValue('product_offering');
        } else {
            return null;
        }

        var grCharsPerSpecOffering = new GlideRecord('sn_prd_pm_product_offering_characteristic');
        grCharsPerSpecOffering.addQuery('product_offering', productOffering);
        grCharsPerSpecOffering.addQuery('specification', specification);
        grCharsPerSpecOffering.addQuery('characteristic', characteristic);
        grCharsPerSpecOffering.query();
        while (grCharsPerSpecOffering.next()) {
            validCharOptions.push(grCharsPerSpecOffering.getDisplayValue('characteristic_option'));
        }

        var uniqueValidCharOptions = new global.ArrayUtil().unique(validCharOptions);

        if (uniqueValidCharOptions.indexOf(charValue) > -1) {
            validCharValue = true;
        }

        var results = {
            "validCharValue": validCharValue,
            "validCharOptions": validCharOptions
        };
        return JSON.stringify(results);
    },

    type: 'ItemCharUtils'
});
