sys_ui_action.script:
function generateChars() {
    var answer = confirm(getMessage("Please confirm you wish to re-generate this item's characteristics."));
    if (answer == true) {
        gsftSubmit(null, g_form.getFormElement(), 'generate_characteristics');
    } else {
        return false;
    }
}

if (typeof window == 'undefined') {
    generateCharsServerScript();
}

function generateCharsServerScript() {
    var itemCharUtils = new x_648117_tmf_mock.ItemCharacteristicUtils();

    var item = current.getUniqueValue();
    var productOffering = current.getValue('product_offering');
    var specification = current.getValue('specification');
    var populateData = current.getValue('populate_chars');

	// Delete exisiting chars
	var itemCharsDelete = new GlideRecord('x_648117_tmf_mock_item_characteristic');
	itemCharsDelete.addQuery('item', item);
	itemCharsDelete.query();
	itemCharsDelete.deleteMultiple();

    // Get chars as an array of objects
    var characteristics = itemCharUtils.getProductOfferingCharacteristics(productOffering, specification);

    // Generate record for each characteristic if not found
    if (characteristics !== null) {
        for (i = 0; i < characteristics.length; i++) {
            var grItemChars = new GlideRecord('x_648117_tmf_mock_item_characteristic');
            grItemChars.addQuery('item', item);
            grItemChars.addQuery('characteristic', characteristics[i].characteristic);
			grItemChars.query();
            if (!grItemChars.next()) {
                itemCharUtils.generateItemCharacteristic(characteristics[i], item, populateData);
            }
        }
    }
	action.setRedirectURL("x_648117_tmf_mock_items.do?sys_id=" + current.getUniqueValue());
}
