sys_ui_action.script:
function sendAPI() {
    var answer = confirm("Confirm you want to attempt a REST API call with this payload.");
    if (answer == true) {
        gsftSubmit(null, g_form.getFormElement(), 'send_api_request');
    } else {
        return false;
    }
}

if (typeof window == 'undefined') {
    sendAPISS();
}

function sendAPISS() {
	var instanceURL = gs.getProperty('glide.servlet.uri');
	var request = new sn_ws.RESTMessageV2();
	request.setEndpoint(instanceURL + 'api/sn_ind_tmt_orm/order/productOrder');
	request.setHttpMethod('POST');
	request.setAuthenticationProfile('basic', gs.getProperty('x_648117_tmf_mock.basic_auth_config'));
	request.setRequestHeader("Accept","application/json");
	request.setRequestBody(current.getValue('payload').toString());
	var response = request.execute();
	var statusCode = response.getStatusCode();
	var responseBody = JSON.parse(response.getBody());
	if(statusCode == '200' || statusCode == '201'){
		var id = responseBody.id.toString();
		gs.addInfoMessage(gs.getMessage("Order was generated with ID: ") + id);
	} else {
		if(responseBody.details[0].message){
			var error_msg = responseBody.details[0].message;
			gs.addErrorMessage(gs.getMessage("The API encountered the following error: " + error_msg));
		} else {
			gs.addErrorMessage(gs.getMessage("There was an error with the API. Please contact an admin to troubleshoot."));
		}
	}
    action.setRedirectURL(current);
}
